import React, { useState, useRef, useCallback } from 'react';
import html2canvas from 'html2canvas';
import './index.css';

function App() {
  const [url, setUrl] = useState('');
  const [currentUrl, setCurrentUrl] = useState('');
  const [designImage, setDesignImage] = useState(null);
  const [imageOpacity, setImageOpacity] = useState(0.5);
  const [imageSize, setImageSize] = useState({ width: 400, height: 300 });
  const [imagePosition, setImagePosition] = useState({ x: 0, y: 0 });
  const [imageScale, setImageScale] = useState(1);
  const [imageTransformOrigin, setImageTransformOrigin] = useState('center center');
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [dragStart, setDragStart] = useState(null);
  const [resizeStart, setResizeStart] = useState(null);
  const [isSelecting, setIsSelecting] = useState(false);
  const [selection, setSelection] = useState(null);
  const [selectionStart, setSelectionStart] = useState(null);
  const [issues, setIssues] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalScreenshot, setModalScreenshot] = useState(null);
  const [currentIssue, setCurrentIssue] = useState({ description: '', suggestion: '' });
  const [editingIssueId, setEditingIssueId] = useState(null);
  const [designSize, setDesignSize] = useState({ width: 1440, height: 900 });

  const previewRef = useRef();
  const fileInputRef = useRef();
  const iframeRef = useRef();

  // 常见Web端设计尺寸
  const designSizePresets = [
    { name: '1440×900 (主流)', width: 1440, height: 900 },
    { name: '1920×1080 (桌面)', width: 1920, height: 1080 },
    { name: '1366×768 (笔记本)', width: 1366, height: 768 },
    { name: '1280×720 (小屏)', width: 1280, height: 720 },
    { name: '1600×900 (宽屏)', width: 1600, height: 900 }
  ];

  const loadUrl = () => {
    if (url.trim()) {
      setCurrentUrl(url.trim());
    }
  };

  const clearPage = () => {
    setUrl('');
    setCurrentUrl('');
    setDesignImage(null);
    setImagePosition({ x: 0, y: 0 });
    setImageSize({ width: 400, height: 300 });
    setImageScale(1);
    setImageTransformOrigin('center center');
    setSelection(null);
    setSelectionStart(null);
    setIsSelecting(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // 切换设计尺寸
  const handleDesignSizeChange = (event) => {
    const selectedIndex = event.target.value;
    const selectedPreset = designSizePresets[selectedIndex];
    setDesignSize({ width: selectedPreset.width, height: selectedPreset.height });
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const img = new Image();
        img.onload = () => {
          setDesignImage(e.target.result);

          // 设置初始尺寸
          const maxWidth = 500;
          const maxHeight = 400;
          let width = img.width;
          let height = img.height;

          // 如果图片太大，按比例缩小
          if (width > maxWidth || height > maxHeight) {
            const scaleX = maxWidth / width;
            const scaleY = maxHeight / height;
            const scale = Math.min(scaleX, scaleY);
            width = Math.round(width * scale);
            height = Math.round(height * scale);
          }

          setImageSize({ width, height });
          setImagePosition({ x: 100, y: 100 });
        };
        img.src = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  };

  const triggerImageUpload = () => {
    fileInputRef.current.click();
  };

  const removeDesignImage = () => {
    setDesignImage(null);
    setImagePosition({ x: 0, y: 0 });
    setImageSize({ width: 400, height: 300 });
    setImageScale(1);
    setImageTransformOrigin('center center');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleImageMouseDown = (e) => {
    // 检查是否点击在resize handle上
    if (e.target.classList.contains('resize-handle') || 
        e.target.closest('.resize-handle')) {
      return; // 避免和缩放冲突
    }

    // 确保点击的是图片本身或图片容器
    if (!e.target.classList.contains('design-image') && 
        !e.target.classList.contains('design-overlay')) {
      return;
    }

    e.preventDefault();
    e.stopPropagation();

    // 添加更严格的状态检查
    if (isResizing || isSelecting) {
      return;
    }

    setIsDragging(true);
    setDragStart({
      x: e.clientX - imagePosition.x,
      y: e.clientY - imagePosition.y
    });
  };

  const handleResizeMouseDown = (e, direction) => {
    e.preventDefault();
    e.stopPropagation();

    // 添加更严格的状态检查
    if (isDragging || isSelecting || isResizing) {
      return;
    }

    setIsResizing(true);

    // 设置transform-origin根据方向
    let origin = 'center center';
    switch (direction) {
      case 'nw': origin = 'bottom right'; break;
      case 'ne': origin = 'bottom left'; break;
      case 'sw': origin = 'top right'; break;
      case 'se': origin = 'top left'; break;
      case 'n': origin = 'bottom center'; break;
      case 's': origin = 'top center'; break;
      case 'w': origin = 'right center'; break;
      case 'e': origin = 'left center'; break;
    }
    setImageTransformOrigin(origin);

    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      direction: direction,
      startScale: imageScale
    });
  };

  const handleMouseMove = useCallback((e) => {
    // 直接处理拖拽
    if (isDragging && dragStart) {
      const newX = e.clientX - dragStart.x;
      const newY = e.clientY - dragStart.y;
      setImagePosition({ x: newX, y: newY });
      return;
    }

    // 直接处理选择
    if (isSelecting && selectionStart) {
      const rect = previewRef.current?.getBoundingClientRect();
      if (rect) {
        const currentX = e.clientX - rect.left;
        const currentY = e.clientY - rect.top;

        setSelection({
          left: Math.min(selectionStart.x, currentX),
          top: Math.min(selectionStart.y, currentY),
          width: Math.abs(currentX - selectionStart.x),
          height: Math.abs(currentY - selectionStart.y)
        });
      }
      return;
    }

    // 使用transform缩放处理
    if (isResizing && resizeStart) {
      const deltaX = e.clientX - resizeStart.x;
      const deltaY = e.clientY - resizeStart.y;

      // 根据拖拽方向计算缩放增量 - 提高灵敏度
      let scaleChange = 0;
      const sensitivity = 100; // 降低数值提高灵敏度

      switch (resizeStart.direction) {
        case 'se': // 右下角 - 向右下拖拽放大
          scaleChange = Math.max(deltaX, deltaY) / sensitivity;
          break;
        case 'nw': // 左上角 - 向左上拖拽放大
          scaleChange = Math.max(-deltaX, -deltaY) / sensitivity;
          break;
        case 'ne': // 右上角 - 向右上拖拽放大
          scaleChange = Math.max(deltaX, -deltaY) / sensitivity;
          break;
        case 'sw': // 左下角 - 向左下拖拽放大
          scaleChange = Math.max(-deltaX, deltaY) / sensitivity;
          break;
        case 'e': // 右边 - 向右拖拽放大
          scaleChange = deltaX / sensitivity;
          break;
        case 'w': // 左边 - 向左拖拽放大
          scaleChange = -deltaX / sensitivity;
          break;
        case 's': // 下边 - 向下拖拽放大
          scaleChange = deltaY / sensitivity;
          break;
        case 'n': // 上边 - 向上拖拽放大
          scaleChange = -deltaY / sensitivity;
          break;
      }

      // 计算新的缩放值，增加更大的范围
      const newScale = Math.max(0.1, Math.min(10, resizeStart.startScale + scaleChange));
      setImageScale(newScale);
    }
  }, [isDragging, dragStart, isResizing, resizeStart, isSelecting, selectionStart]);

  const handleMouseUp = useCallback(async () => {
    // 保存当前状态
    const wasDragging = isDragging;
    const wasResizing = isResizing;
    const wasSelecting = isSelecting;

    // 立即清理所有状态，防止状态残留
    setIsDragging(false);
    setIsResizing(false);
    setDragStart(null);
    setResizeStart(null);

    if (wasResizing) {
      // 缩放结束后重置transform-origin
      setTimeout(() => setImageTransformOrigin('center center'), 50);
    }

    if (isSelecting && selection && selection.width > 10 && selection.height > 10) {
      setIsSelecting(false);

      try {
        const canvas = await html2canvas(previewRef.current, {
          useCORS: true,
          allowTaint: true,
          scale: 1
        });

        const screenshot = canvas.toDataURL('image/png');
        setModalScreenshot(screenshot);
        setShowModal(true);
        setCurrentIssue({ description: '', suggestion: '' });
        setEditingIssueId(null);
      } catch (error) {
        console.error('截图失败:', error);
        alert('截图失败，请重试');
      }
    }
  }, [isSelecting, selection]);

  React.useEffect(() => {
    if (isDragging || isResizing || isSelecting) {
      const handleGlobalMouseMove = (e) => {
        handleMouseMove(e);
      };

      const handleGlobalMouseUp = (e) => {
        handleMouseUp(e);
      };

      // 添加全局事件监听，包括window事件
      document.addEventListener('mousemove', handleGlobalMouseMove);
      document.addEventListener('mouseup', handleGlobalMouseUp);
      window.addEventListener('blur', handleGlobalMouseUp); // 窗口失焦时清理
      document.addEventListener('mouseleave', handleGlobalMouseUp); // 鼠标离开文档时清理

      return () => {
        document.removeEventListener('mousemove', handleGlobalMouseMove);
        document.removeEventListener('mouseup', handleGlobalMouseUp);
        window.removeEventListener('blur', handleGlobalMouseUp);
        document.removeEventListener('mouseleave', handleGlobalMouseUp);
      };
    }
  }, [isDragging, isResizing, isSelecting, handleMouseMove, handleMouseUp]);


  const startSelection = () => {
    setIsSelecting(true);
    setSelection(null);
  };

  const cancelSelection = () => {
    setIsSelecting(false);
    setSelection(null);
    setSelectionStart(null);
  };

  const handlePreviewMouseDown = (e) => {
    if (isSelecting) {
      const rect = previewRef.current.getBoundingClientRect();
      setSelectionStart({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };


  const saveIssue = () => {
    if (currentIssue.description.trim() || currentIssue.suggestion.trim()) {
      const newIssue = {
        id: editingIssueId || Date.now().toString(),
        screenshot: modalScreenshot,
        description: currentIssue.description,
        suggestion: currentIssue.suggestion,
        status: '未验收'
      };

      if (editingIssueId) {
        setIssues(issues.map(issue => 
          issue.id === editingIssueId ? newIssue : issue
        ));
      } else {
        setIssues([...issues, newIssue]);
      }
    }

    setShowModal(false);
    setSelection(null);
    setSelectionStart(null);
    setModalScreenshot(null);
    setCurrentIssue({ description: '', suggestion: '' });
    setEditingIssueId(null);
  };

  const editIssue = (issue) => {
    setCurrentIssue({
      description: issue.description,
      suggestion: issue.suggestion
    });
    setModalScreenshot(issue.screenshot);
    setEditingIssueId(issue.id);
    setShowModal(true);
  };

  const deleteIssue = (issueId) => {
    if (window.confirm('确定要删除这个问题吗？')) {
      setIssues(issues.filter(issue => issue.id !== issueId));
    }
  };

  // 将图片压缩为小尺寸缩略图
  const compressImage = (base64, maxWidth = 150, maxHeight = 100, quality = 0.8) => {
    return new Promise((resolve) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = () => {
        // 计算压缩后的尺寸
        let { width, height } = img;
        const ratio = Math.min(maxWidth / width, maxHeight / height);
        width = Math.round(width * ratio);
        height = Math.round(height * ratio);

        canvas.width = width;
        canvas.height = height;

        // 绘制压缩图片
        ctx.drawImage(img, 0, 0, width, height);

        // 转为较低质量的base64
        const compressedBase64 = canvas.toDataURL('image/jpeg', quality);
        resolve(compressedBase64);
      };

      img.src = base64;
    });
  };

  const exportToReport = () => {
    if (issues.length === 0) {
      alert('没有问题记录可以导出');
      return;
    }

    try {
      // 直接使用原始图片，不压缩
      let html = `
        <html>
        <head>
          <meta charset="utf-8">
          <style>
            body { 
              font-family: 'Microsoft YaHei', Arial, sans-serif; 
              margin: 40px;
              line-height: 1.6;
            }
            .issue-item {
              margin-bottom: 50px;
              page-break-inside: avoid;
              border: 1px solid #ddd;
              border-radius: 8px;
              padding: 25px;
              background-color: #fafafa;
            }
            .issue-header {
              background-color: #4472C4;
              color: white;
              padding: 18px 25px;
              margin: -25px -25px 30px -25px;
              border-radius: 7px 7px 0 0;
              font-size: 42px;
              font-weight: bold;
              text-align: center;
            }
            .screenshot {
              max-width: 600px;
              height: auto;
              border: 2px solid #ccc;
              border-radius: 4px;
              display: block;
              margin: 20px auto;
              box-shadow: 0 2px 8px rgba(0,0,0,0.1);
            }
            .info-section {
              margin-top: 25px;
              background-color: #f8f9fa;
              padding: 15px;
              border-radius: 5px;
            }
            .info-label {
              font-weight: bold;
              color: #333;
              margin-bottom: 15px;
              font-size: 32px;
            }
            .info-content {
              background-color: white;
              padding: 25px;
              border: 2px solid #ddd;
              border-radius: 6px;
              margin-bottom: 30px;
              min-height: 80px;
              font-size: 28px;
              line-height: 2.0;
            }
          </style>
        </head>
        <body>
      `;

      issues.forEach((issue, index) => {
        const status = issue.status || '未修改';

        html += `
          <div class="issue-item">
            <div class="issue-header">
              问题 ${index + 1}
            </div>

            <div style="text-align: center;">
              <img class="screenshot" src="${issue.screenshot}" alt="问题${index + 1}截图" />
            </div>

            <div class="info-section">
              <div class="info-label">问题描述：</div>
              <div class="info-content">${issue.description || '无描述'}</div>

              <div class="info-label">修改建议：</div>
              <div class="info-content">${issue.suggestion || '无建议'}</div>

              <div class="info-label">验收状态：</div>
              <div class="info-content">未验收</div>
            </div>
          </div>
        `;
      });

      html += `
        </body>
        </html>
      `;

      // 创建Blob并下载为.doc文件（Word兼容的HTML格式）
      const blob = new Blob([html], { 
        type: 'application/msword;charset=utf-8' 
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `UI走查报告_${new Date().toISOString().slice(0, 10)}.doc`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

    } catch (error) {
      console.error('导出Excel失败:', error);
      alert('导出Excel失败，请重试：' + error.message);
    }
  };

  return (
    <div className="app">
      <div className="top-bar">
        <input
          type="text"
          className="url-input"
          placeholder="请输入前端测试页面的URL地址..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && loadUrl()}
        />
        <button className="load-button" onClick={loadUrl}>
          加载页面
        </button>
        <button className="load-button" onClick={clearPage} style={{backgroundColor: '#6c757d', marginLeft: '10px'}}>
          清空页面
        </button>
      </div>

      <div className="main-content">
        <div className="preview-section">
          <div className="preview-controls">
            <div className="control-group">
              <label>设计尺寸:</label>
              <select 
                className="design-size-selector" 
                onChange={handleDesignSizeChange}
                defaultValue={0}
              >
                {designSizePresets.map((preset, index) => (
                  <option key={index} value={index}>
                    {preset.name}
                  </option>
                ))}
              </select>
            </div>

            <input
              type="file"
              ref={fileInputRef}
              className="file-input"
              accept=".png,.jpg,.jpeg"
              onChange={handleImageUpload}
            />
            <button 
              className="upload-button" 
              onClick={triggerImageUpload}
              title="支持格式：PNG, JPG, JPEG"
            >
              上传设计图
            </button>

            <button 
              className="upload-button" 
              onClick={isSelecting ? cancelSelection : startSelection}
              style={{backgroundColor: isSelecting ? '#dc3545' : '#ffc107', color: isSelecting ? 'white' : '#212529'}}
              title="支持框选页面问题，并填入具体修改要求"
            >
              {isSelecting ? '取消框选' : '手动框选'}
            </button>

            {designImage && (
              <>
                <button className="upload-button" onClick={removeDesignImage} style={{backgroundColor: '#dc3545'}}>
                  删除设计图
                </button>
                <div className="control-group">
                  <label>透明度:</label>
                  <input
                    type="range"
                    className="control-slider"
                    min="0"
                    max="1"
                    step="0.1"
                    value={imageOpacity}
                    onChange={(e) => setImageOpacity(parseFloat(e.target.value))}
                  />
                  <span>{Math.round((1 - imageOpacity) * 100)}%</span>
                </div>
              </>
            )}
          </div>


          <div 
            className="preview-container" 
            id="previewArea"
            ref={previewRef}
            onMouseDown={handlePreviewMouseDown}
          >
            <div 
              className="iframe-wrapper"
              id="frameWrapper"
              style={{
                width: designSize.width,
                height: designSize.height
              }}
            >
              {currentUrl ? (
                <iframe
                  ref={iframeRef}
                  className="preview-iframe"
                  src={currentUrl}
                  title="网页预览"
                  sandbox="allow-same-origin allow-scripts allow-forms"
                  style={{
                    width: designSize.width,
                    height: designSize.height
                  }}
                />
              ) : (
                <div className="empty-preview-placeholder">
                  <div className="placeholder-content">
                    <h3>欢迎使用UI走查工具</h3>
                    <p>请在上方输入框中粘贴前端测试页面的URL地址</p>
                    <p className="placeholder-example">例如：http://localhost:3000 或 https://example.com</p>
                    <div className="placeholder-steps">
                      <div className="step">1. 输入前端页面URL</div>
                      <div className="step">2. 点击"加载页面"</div>
                      <div className="step">3. 上传设计图进行对比</div>
                      <div className="step">4. 使用"手动框选"标记问题</div>
                    </div>
                  </div>
                </div>
              )}

              {designImage && (
              <div
                className={`design-overlay ${isDragging ? 'dragging' : ''} ${isResizing ? 'resizing' : ''}`}
                style={{
                  left: imagePosition.x,
                  top: imagePosition.y,
                  width: imageSize.width,
                  height: imageSize.height,
                  opacity: imageOpacity,
                  position: 'absolute',
                  transform: `scale(${imageScale})`,
                  transformOrigin: imageTransformOrigin,
                  transition: isResizing ? 'none' : 'transform 0.1s ease'
                }}
                onMouseDown={handleImageMouseDown}
              >
                <img
                  className="design-image"
                  src={designImage}
                  alt="设计图"
                  draggable={false}
                  style={{ 
                    width: '100%',
                    height: '100%',
                    objectFit: 'contain' // 保持宽高比
                  }}
                />

                {/* 8个缩放控制点 */}
                <div className="resize-handle nw" onMouseDown={(e) => handleResizeMouseDown(e, 'nw')} title="拖拽调整大小" />
                <div className="resize-handle n" onMouseDown={(e) => handleResizeMouseDown(e, 'n')} title="拖拽调整大小" />
                <div className="resize-handle ne" onMouseDown={(e) => handleResizeMouseDown(e, 'ne')} title="拖拽调整大小" />
                <div className="resize-handle e" onMouseDown={(e) => handleResizeMouseDown(e, 'e')} title="拖拽调整大小" />
                <div className="resize-handle se" onMouseDown={(e) => handleResizeMouseDown(e, 'se')} title="拖拽调整大小" />
                <div className="resize-handle s" onMouseDown={(e) => handleResizeMouseDown(e, 's')} title="拖拽调整大小" />
                <div className="resize-handle sw" onMouseDown={(e) => handleResizeMouseDown(e, 'sw')} title="拖拽调整大小" />
                <div className="resize-handle w" onMouseDown={(e) => handleResizeMouseDown(e, 'w')} title="拖拽调整大小" />
              </div>
            )}

            <div className={`selection-overlay ${isSelecting ? 'selecting' : ''}`}>
              {selection && (
                <div
                  className="selection-box"
                  style={{
                    left: selection.left,
                    top: selection.top,
                    width: selection.width,
                    height: selection.height
                  }}
                />
              )}
            </div>
            </div>
          </div>
        </div>

        <div className="issues-panel">
          <div className="issues-header">
            <h3 className="issues-title">问题列表 ({issues.length})</h3>
            <button className="export-button" onClick={exportToReport} disabled={issues.length === 0}>
              导出走查报告
            </button>
          </div>

          <div className="issues-list">
            {issues.length === 0 ? (
              <div style={{ textAlign: 'center', color: '#666', marginTop: '50px' }}>
                暂无问题记录
                <br />
                <small>上传设计图后，点击"开始框选"来标记问题区域</small>
              </div>
            ) : (
              issues.map((issue) => (
                <div key={issue.id} className="issue-item">
                  <img
                    className="issue-screenshot"
                    src={issue.screenshot}
                    alt="问题截图"
                  />
                  <div className="issue-description">
                    <div className="issue-label">问题描述:</div>
                    <div className="issue-text">{issue.description || '无描述'}</div>
                  </div>
                  <div className="issue-suggestion">
                    <div className="issue-label">修改建议:</div>
                    <div className="issue-text">{issue.suggestion || '无建议'}</div>
                  </div>
                  <div className="issue-actions">
                    <button className="edit-button" onClick={() => editIssue(issue)}>
                      编辑
                    </button>
                    <button className="delete-button" onClick={() => deleteIssue(issue.id)}>
                      删除
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="modal-title">
              {editingIssueId ? '编辑问题' : '添加问题'}
            </h3>

            <img
              className="modal-screenshot"
              src={modalScreenshot}
              alt="截图"
            />

            <div className="form-group">
              <label className="form-label">问题描述</label>
              <textarea
                className="form-textarea"
                value={currentIssue.description}
                onChange={(e) => setCurrentIssue({...currentIssue, description: e.target.value})}
                placeholder="请具体描述发现的问题，如：按钮颜色不对、文字大小不符合设计稿等"
              />
              <div className="form-placeholder">
                提示：请详细描述问题，便于开发人员理解和修改
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">修改建议</label>
              <textarea
                className="form-textarea"
                value={currentIssue.suggestion}
                onChange={(e) => setCurrentIssue({...currentIssue, suggestion: e.target.value})}
                placeholder="请提供具体的修改建议，如：颜色改为#FF0000、字体大小改为16px等"
              />
              <div className="form-placeholder">
                提示：请提供具体的数值和参数，便于开发人员精确修改
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="modal-button secondary" 
                onClick={() => {
                  setShowModal(false);
                  setSelection(null);
                  setSelectionStart(null);
                  setCurrentIssue({ description: '', suggestion: '' });
                  setEditingIssueId(null);
                }}
              >
                取消
              </button>
              <button className="modal-button primary" onClick={saveIssue}>
                {editingIssueId ? '保存修改' : '添加问题'}
              </button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

export default App;
