import React, { useState, useRef, useCallback } from 'react';
import html2canvas from 'html2canvas';
import './index.css';

function App() {
  const [url, setUrl] = useState('');
  const [currentUrl, setCurrentUrl] = useState('');
  const [designImage, setDesignImage] = useState(null);
  const [imageOpacity, setImageOpacity] = useState(0.5);
  const [imageSize, setImageSize] = useState({ width: 400, height: 300 });
  const [imageAspectRatio, setImageAspectRatio] = useState(1);
  const [imagePosition, setImagePosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [dragStart, setDragStart] = useState(null);
  const [resizeStart, setResizeStart] = useState(null);
  const [isSelecting, setIsSelecting] = useState(false);
  const [selection, setSelection] = useState(null);
  const [selectionStart, setSelectionStart] = useState(null);
  const [issues, setIssues] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalScreenshot, setModalScreenshot] = useState(null);
  const [currentIssue, setCurrentIssue] = useState({ description: '', suggestion: '' });
  const [editingIssueId, setEditingIssueId] = useState(null);
  const [pageSize, setPageSize] = useState({ width: 1440, height: 900 });
  const [autoScale, setAutoScale] = useState(1);
  const [scaleAdjustment, setScaleAdjustment] = useState(1);
  const [showSizeSelector, setShowSizeSelector] = useState(false);
  const [customSize, setCustomSize] = useState({ width: '', height: '' });

  const previewRef = useRef();
  const fileInputRef = useRef();
  const iframeRef = useRef();
  const resizeObserver = useRef(null);

  // 页面尺寸预设
  const sizePresets = [
    { name: '1920×1080 (桌面)', width: 1920, height: 1080 },
    { name: '1440×900 (常见)', width: 1440, height: 900 },
    { name: '1366×768 (笔记本)', width: 1366, height: 768 },
    { name: '1280×1024 (旧屏)', width: 1280, height: 1024 },
    { name: '1600×900 (宽屏)', width: 1600, height: 900 }
  ];

  // 计算最终缩放比例
  const finalScale = autoScale * scaleAdjustment;

  // 从localStorage获取上次的页面尺寸
  const loadSavedSize = useCallback(() => {
    try {
      const saved = localStorage.getItem('ui-inspector-page-size');
      if (saved) {
        const parsedSize = JSON.parse(saved);
        setPageSize(parsedSize);
      }
    } catch (error) {
      console.warn('无法加载保存的页面尺寸');
    }
  }, []);

  // 保存页面尺寸到localStorage
  const savePageSize = useCallback((size) => {
    try {
      localStorage.setItem('ui-inspector-page-size', JSON.stringify(size));
    } catch (error) {
      console.warn('无法保存页面尺寸');
    }
  }, []);

  const loadUrl = () => {
    if (url.trim()) {
      setCurrentUrl(url.trim());
      setScaleAdjustment(1); // 重置微调
    }
  };

  // iframe加载完成，尝试获取页面真实尺寸
  const handleIframeLoad = useCallback(() => {
    try {
      if (iframeRef.current && iframeRef.current.contentWindow) {
        const iframeDoc = iframeRef.current.contentDocument || iframeRef.current.contentWindow.document;
        if (iframeDoc && iframeDoc.body) {
          // 同源情况：可以读取页面尺寸
          const body = iframeDoc.body;
          const html = iframeDoc.documentElement;

          const width = Math.max(
            body.scrollWidth, body.offsetWidth,
            html.clientWidth, html.scrollWidth, html.offsetWidth
          );
          const height = Math.max(
            body.scrollHeight, body.offsetHeight,
            html.clientHeight, html.scrollHeight, html.offsetHeight
          );

          if (width > 0 && height > 0) {
            const detectedSize = { width, height };
            setPageSize(detectedSize);
            savePageSize(detectedSize);
            console.log('检测到页面尺寸:', detectedSize);
            return;
          }
        }
      }
    } catch (error) {
      // 跨域限制，显示尺寸选择器
      console.log('跨域限制，显示尺寸选择器');
      setShowSizeSelector(true);
    }
  }, [savePageSize]);

  // 计算自动缩放比例
  const calculateAutoScale = useCallback(() => {
    if (previewRef.current) {
      const container = previewRef.current;
      const containerRect = container.getBoundingClientRect();

      // 减去padding的空间
      const availableWidth = containerRect.width - 40; // 20px padding * 2
      const availableHeight = containerRect.height - 40; // 20px padding * 2

      const scaleX = availableWidth / pageSize.width;
      const scaleY = availableHeight / pageSize.height;

      // 取较小的比例以保证完整显示
      const newAutoScale = Math.min(scaleX, scaleY, 1); // 最大不超过1

      setAutoScale(newAutoScale);
    }
  }, [pageSize.width, pageSize.height]);

  // 缩放微调
  const adjustScale = (factor) => {
    setScaleAdjustment(prev => Math.max(0.1, Math.min(3, prev * factor)));
  };

  // 重置为自动比例
  const resetScale = () => {
    setScaleAdjustment(1);
  };

  // 选择预设尺寸
  const selectPresetSize = (preset) => {
    const newSize = { width: preset.width, height: preset.height };
    setPageSize(newSize);
    savePageSize(newSize);
    setShowSizeSelector(false);
  };

  // 应用自定义尺寸
  const applyCustomSize = () => {
    const width = parseInt(customSize.width);
    const height = parseInt(customSize.height);

    if (width > 0 && height > 0) {
      const newSize = { width, height };
      setPageSize(newSize);
      savePageSize(newSize);
      setShowSizeSelector(false);
      setCustomSize({ width: '', height: '' });
    } else {
      alert('请输入有效的宽度和高度');
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const img = new Image();
        img.onload = () => {
          setDesignImage(e.target.result);

          // 计算宽高比
          const aspectRatio = img.width / img.height;
          setImageAspectRatio(aspectRatio);

          // 设置初始尺寸
          const maxWidth = 500;
          const maxHeight = 400;
          let width = img.width;
          let height = img.height;

          // 如果图片太大，按比例缩小
          if (width > maxWidth || height > maxHeight) {
            const scaleX = maxWidth / width;
            const scaleY = maxHeight / height;
            const scale = Math.min(scaleX, scaleY);
            width = Math.round(width * scale);
            height = Math.round(height * scale);
          }

          setImageSize({ width, height });
          setImagePosition({ x: 100, y: 100 });
        };
        img.src = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  };

  const triggerImageUpload = () => {
    fileInputRef.current.click();
  };

  const removeDesignImage = () => {
    setDesignImage(null);
    setImagePosition({ x: 0, y: 0 });
    setImageSize({ width: 400, height: 300 });
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleImageMouseDown = (e) => {
    if (e.target.classList.contains('resize-handle')) return; // 避免和缩放冲突
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
    setDragStart({
      x: e.clientX - imagePosition.x,
      y: e.clientY - imagePosition.y
    });
  };

  const handleResizeMouseDown = (e, direction) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);

    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      direction: direction,
      startSize: { ...imageSize },
      startPosition: { ...imagePosition }
    });
  };

  const handleMouseMove = useCallback((e) => {
    if (isDragging && dragStart) {
      setImagePosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }

    if (isResizing && resizeStart) {
      const deltaX = e.clientX - resizeStart.x;
      const deltaY = e.clientY - resizeStart.y;

      // 计算主要的缩放变化（取X和Y的平均值以保持等比例）
      const delta = (deltaX + deltaY) / 2;
      let scale = 1 + (delta / 200); // 缩放敏感度

      // 根据不同方向调整缩放方向
      switch (resizeStart.direction) {
        case 'se': // 右下角
          break; // 默认正向缩放
        case 'nw': // 左上角
          break; // 正向缩放
        case 'sw': // 左下角
          scale = 1 + ((-deltaX + deltaY) / 400);
          break;
        case 'ne': // 右上角
          scale = 1 + ((deltaX - deltaY) / 400);
          break;
        case 'e': // 右边
          scale = 1 + (deltaX / 200);
          break;
        case 'w': // 左边
          scale = 1 + (-deltaX / 200);
          break;
        case 's': // 下边
          scale = 1 + (deltaY / 200);
          break;
        case 'n': // 上边
          scale = 1 + (-deltaY / 200);
          break;
        default:
          break;
      }

      // 限制缩放范围
      scale = Math.max(0.2, Math.min(5, scale));

      // 计算新的等比例尺寸
      let newWidth = Math.round(resizeStart.startSize.width * scale);
      let newHeight = Math.round(newWidth / imageAspectRatio);

      // 最小尺寸限制
      newWidth = Math.max(50, newWidth);
      newHeight = Math.max(30, newHeight);

      // 根据缩放方向调整位置（确保对应的边固定不动）
      let newX = resizeStart.startPosition.x;
      let newY = resizeStart.startPosition.y;

      if (['nw', 'n', 'ne'].includes(resizeStart.direction)) {
        // 上边固定，调整Y位置
        newY = resizeStart.startPosition.y + (resizeStart.startSize.height - newHeight);
      }

      if (['nw', 'w', 'sw'].includes(resizeStart.direction)) {
        // 左边固定，调整X位置
        newX = resizeStart.startPosition.x + (resizeStart.startSize.width - newWidth);
      }

      setImageSize({ width: newWidth, height: newHeight });
      setImagePosition({ x: newX, y: newY });
    }

    if (isSelecting && selectionStart) {
      const rect = previewRef.current.getBoundingClientRect();
      const currentX = e.clientX - rect.left;
      const currentY = e.clientY - rect.top;

      setSelection({
        left: Math.min(selectionStart.x, currentX),
        top: Math.min(selectionStart.y, currentY),
        width: Math.abs(currentX - selectionStart.x),
        height: Math.abs(currentY - selectionStart.y)
      });
    }
  }, [isDragging, dragStart, isResizing, resizeStart, isSelecting, selectionStart, imageAspectRatio]);

  const handleMouseUp = useCallback(async () => {
    setIsDragging(false);
    setIsResizing(false);
    setDragStart(null);
    setResizeStart(null);

    if (isSelecting && selection && selection.width > 10 && selection.height > 10) {
      setIsSelecting(false);

      try {
        const canvas = await html2canvas(previewRef.current, {
          useCORS: true,
          allowTaint: true,
          scale: 1
        });

        const screenshot = canvas.toDataURL('image/png');
        setModalScreenshot(screenshot);
        setShowModal(true);
        setCurrentIssue({ description: '', suggestion: '' });
        setEditingIssueId(null);
      } catch (error) {
        console.error('截图失败:', error);
        alert('截图失败，请重试');
      }
    }
  }, [isSelecting, selection]);

  React.useEffect(() => {
    if (isDragging || isResizing || isSelecting) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);

      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, isResizing, isSelecting, handleMouseMove, handleMouseUp]);

  // 监听容器和窗口尺寸变化
  React.useEffect(() => {
    calculateScale();

    const handleResize = () => {
      calculateScale();
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [calculateScale]);

  // 基准视口变化时重新计算
  React.useEffect(() => {
    calculateScale();
  }, [baseViewport, calculateScale]);

  const startSelection = () => {
    setIsSelecting(true);
    setSelection(null);
  };

  const cancelSelection = () => {
    setIsSelecting(false);
    setSelection(null);
    setSelectionStart(null);
  };

  const handlePreviewMouseDown = (e) => {
    if (isSelecting) {
      const rect = previewRef.current.getBoundingClientRect();
      setSelectionStart({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };


  const saveIssue = () => {
    if (currentIssue.description.trim() || currentIssue.suggestion.trim()) {
      const newIssue = {
        id: editingIssueId || Date.now().toString(),
        screenshot: modalScreenshot,
        description: currentIssue.description,
        suggestion: currentIssue.suggestion,
        status: '未验收'
      };

      if (editingIssueId) {
        setIssues(issues.map(issue => 
          issue.id === editingIssueId ? newIssue : issue
        ));
      } else {
        setIssues([...issues, newIssue]);
      }
    }

    setShowModal(false);
    setSelection(null);
    setSelectionStart(null);
    setModalScreenshot(null);
    setCurrentIssue({ description: '', suggestion: '' });
    setEditingIssueId(null);
  };

  const editIssue = (issue) => {
    setCurrentIssue({
      description: issue.description,
      suggestion: issue.suggestion
    });
    setModalScreenshot(issue.screenshot);
    setEditingIssueId(issue.id);
    setShowModal(true);
  };

  const deleteIssue = (issueId) => {
    if (window.confirm('确定要删除这个问题吗？')) {
      setIssues(issues.filter(issue => issue.id !== issueId));
    }
  };

  // 将图片压缩为小尺寸缩略图
  const compressImage = (base64, maxWidth = 150, maxHeight = 100, quality = 0.8) => {
    return new Promise((resolve) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = () => {
        // 计算压缩后的尺寸
        let { width, height } = img;
        const ratio = Math.min(maxWidth / width, maxHeight / height);
        width = Math.round(width * ratio);
        height = Math.round(height * ratio);

        canvas.width = width;
        canvas.height = height;

        // 绘制压缩图片
        ctx.drawImage(img, 0, 0, width, height);

        // 转为较低质量的base64
        const compressedBase64 = canvas.toDataURL('image/jpeg', quality);
        resolve(compressedBase64);
      };

      img.src = base64;
    });
  };

  const exportToReport = () => {
    if (issues.length === 0) {
      alert('没有问题记录可以导出');
      return;
    }

    try {
      // 直接使用原始图片，不压缩
      let html = `
        <html>
        <head>
          <meta charset="utf-8">
          <style>
            body { 
              font-family: 'Microsoft YaHei', Arial, sans-serif; 
              margin: 40px;
              line-height: 1.6;
            }
            .issue-item {
              margin-bottom: 50px;
              page-break-inside: avoid;
              border: 1px solid #ddd;
              border-radius: 8px;
              padding: 25px;
              background-color: #fafafa;
            }
            .issue-header {
              background-color: #4472C4;
              color: white;
              padding: 18px 25px;
              margin: -25px -25px 30px -25px;
              border-radius: 7px 7px 0 0;
              font-size: 42px;
              font-weight: bold;
              text-align: center;
            }
            .screenshot {
              max-width: 600px;
              height: auto;
              border: 2px solid #ccc;
              border-radius: 4px;
              display: block;
              margin: 20px auto;
              box-shadow: 0 2px 8px rgba(0,0,0,0.1);
            }
            .info-section {
              margin-top: 25px;
              background-color: #f8f9fa;
              padding: 15px;
              border-radius: 5px;
            }
            .info-label {
              font-weight: bold;
              color: #333;
              margin-bottom: 15px;
              font-size: 32px;
            }
            .info-content {
              background-color: white;
              padding: 25px;
              border: 2px solid #ddd;
              border-radius: 6px;
              margin-bottom: 30px;
              min-height: 80px;
              font-size: 28px;
              line-height: 2.0;
            }
          </style>
        </head>
        <body>
      `;

      issues.forEach((issue, index) => {
        const status = issue.status || '未修改';

        html += `
          <div class="issue-item">
            <div class="issue-header">
              问题 ${index + 1}
            </div>

            <div style="text-align: center;">
              <img class="screenshot" src="${issue.screenshot}" alt="问题${index + 1}截图" />
            </div>

            <div class="info-section">
              <div class="info-label">问题描述：</div>
              <div class="info-content">${issue.description || '无描述'}</div>

              <div class="info-label">修改建议：</div>
              <div class="info-content">${issue.suggestion || '无建议'}</div>

              <div class="info-label">验收状态：</div>
              <div class="info-content">未验收</div>
            </div>
          </div>
        `;
      });

      html += `
        </body>
        </html>
      `;

      // 创建Blob并下载为.doc文件（Word兼容的HTML格式）
      const blob = new Blob([html], { 
        type: 'application/msword;charset=utf-8' 
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `UI走查报告_${new Date().toISOString().slice(0, 10)}.doc`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

    } catch (error) {
      console.error('导出Excel失败:', error);
      alert('导出Excel失败，请重试：' + error.message);
    }
  };

  return (
    <div className="app">
      <div className="top-bar">
        <input
          type="text"
          className="url-input"
          placeholder="请输入前端测试页面的URL地址..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && loadUrl()}
        />
        <button className="load-button" onClick={loadUrl}>
          加载页面
        </button>
      </div>

      <div className="main-content">
        <div className="preview-section">
          <div className="preview-controls">
            <div className="control-group">
              <label>基准视口:</label>
              <select 
                className="viewport-selector" 
                onChange={handleViewportChange}
                defaultValue={0}
              >
                {viewportPresets.map((preset, index) => (
                  <option key={index} value={index}>
                    {preset.name}
                  </option>
                ))}
              </select>
              <span className="scale-info">缩放: {Math.round(scale * 100)}%</span>
            </div>

            <input
              type="file"
              ref={fileInputRef}
              className="file-input"
              accept="image/*"
              onChange={handleImageUpload}
            />
            <button className="upload-button" onClick={triggerImageUpload}>
              上传设计图
            </button>
            {designImage && (
              <>
                <button className="upload-button" onClick={removeDesignImage} style={{backgroundColor: '#dc3545'}}>
                  删除设计图
                </button>
                <div className="control-group">
                  <label>透明度:</label>
                  <input
                    type="range"
                    className="control-slider"
                    min="0"
                    max="1"
                    step="0.1"
                    value={imageOpacity}
                    onChange={(e) => setImageOpacity(parseFloat(e.target.value))}
                  />
                  <span>{Math.round(imageOpacity * 100)}%</span>
                </div>
                <button 
                  className="upload-button" 
                  onClick={isSelecting ? cancelSelection : startSelection}
                  style={{backgroundColor: isSelecting ? '#dc3545' : '#ffc107', color: isSelecting ? 'white' : '#212529'}}
                >
                  {isSelecting ? '取消框选' : '开始框选'}
                </button>
              </>
            )}
          </div>


          <div 
            className="preview-container" 
            ref={previewRef}
            onMouseDown={handlePreviewMouseDown}
          >
            <div 
              className="iframe-wrapper"
              style={{
                width: baseViewport.width,
                height: baseViewport.height,
                transform: `scale(${scale})`,
                transformOrigin: 'top left'
              }}
            >
              {currentUrl && (
                <iframe
                  ref={iframeRef}
                  className="preview-iframe"
                  src={currentUrl}
                  title="网页预览"
                  sandbox="allow-same-origin allow-scripts allow-forms"
                  style={{
                    width: baseViewport.width,
                    height: baseViewport.height
                  }}
                />
              )}

              {designImage && (
              <div
                className={`design-overlay ${isDragging ? 'dragging' : ''} ${isResizing ? 'resizing' : ''}`}
                style={{
                  left: imagePosition.x,
                  top: imagePosition.y,
                  width: imageSize.width,
                  height: imageSize.height,
                  opacity: imageOpacity,
                  position: 'absolute'
                }}
                onMouseDown={handleImageMouseDown}
              >
                <img
                  className="design-image"
                  src={designImage}
                  alt="设计图"
                  draggable={false}
                  style={{ 
                    width: '100%',
                    height: '100%',
                    objectFit: 'contain' // 保持宽高比
                  }}
                />

                {/* 8个缩放控制点 */}
                <div className="resize-handle nw" onMouseDown={(e) => handleResizeMouseDown(e, 'nw')} title="拖拽调整大小" />
                <div className="resize-handle n" onMouseDown={(e) => handleResizeMouseDown(e, 'n')} title="拖拽调整大小" />
                <div className="resize-handle ne" onMouseDown={(e) => handleResizeMouseDown(e, 'ne')} title="拖拽调整大小" />
                <div className="resize-handle e" onMouseDown={(e) => handleResizeMouseDown(e, 'e')} title="拖拽调整大小" />
                <div className="resize-handle se" onMouseDown={(e) => handleResizeMouseDown(e, 'se')} title="拖拽调整大小" />
                <div className="resize-handle s" onMouseDown={(e) => handleResizeMouseDown(e, 's')} title="拖拽调整大小" />
                <div className="resize-handle sw" onMouseDown={(e) => handleResizeMouseDown(e, 'sw')} title="拖拽调整大小" />
                <div className="resize-handle w" onMouseDown={(e) => handleResizeMouseDown(e, 'w')} title="拖拽调整大小" />
              </div>
            )}

            <div className={`selection-overlay ${isSelecting ? 'selecting' : ''}`}>
              {selection && (
                <div
                  className="selection-box"
                  style={{
                    left: selection.left,
                    top: selection.top,
                    width: selection.width,
                    height: selection.height
                  }}
                />
              )}
            </div>
            </div>
          </div>
        </div>

        <div className="issues-panel">
          <div className="issues-header">
            <h3 className="issues-title">问题列表 ({issues.length})</h3>
            <button className="export-button" onClick={exportToReport} disabled={issues.length === 0}>
              导出走查报告
            </button>
          </div>

          <div className="issues-list">
            {issues.length === 0 ? (
              <div style={{ textAlign: 'center', color: '#666', marginTop: '50px' }}>
                暂无问题记录
                <br />
                <small>上传设计图后，点击"开始框选"来标记问题区域</small>
              </div>
            ) : (
              issues.map((issue) => (
                <div key={issue.id} className="issue-item">
                  <img
                    className="issue-screenshot"
                    src={issue.screenshot}
                    alt="问题截图"
                  />
                  <div className="issue-description">
                    <div className="issue-label">问题描述:</div>
                    <div className="issue-text">{issue.description || '无描述'}</div>
                  </div>
                  <div className="issue-suggestion">
                    <div className="issue-label">修改建议:</div>
                    <div className="issue-text">{issue.suggestion || '无建议'}</div>
                  </div>
                  <div className="issue-actions">
                    <button className="edit-button" onClick={() => editIssue(issue)}>
                      编辑
                    </button>
                    <button className="delete-button" onClick={() => deleteIssue(issue.id)}>
                      删除
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="modal-title">
              {editingIssueId ? '编辑问题' : '添加问题'}
            </h3>

            <img
              className="modal-screenshot"
              src={modalScreenshot}
              alt="截图"
            />

            <div className="form-group">
              <label className="form-label">问题描述</label>
              <textarea
                className="form-textarea"
                value={currentIssue.description}
                onChange={(e) => setCurrentIssue({...currentIssue, description: e.target.value})}
                placeholder="请具体描述发现的问题，如：按钮颜色不对、文字大小不符合设计稿等"
              />
              <div className="form-placeholder">
                提示：请详细描述问题，便于开发人员理解和修改
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">修改建议</label>
              <textarea
                className="form-textarea"
                value={currentIssue.suggestion}
                onChange={(e) => setCurrentIssue({...currentIssue, suggestion: e.target.value})}
                placeholder="请提供具体的修改建议，如：颜色改为#FF0000、字体大小改为16px等"
              />
              <div className="form-placeholder">
                提示：请提供具体的数值和参数，便于开发人员精确修改
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="modal-button secondary" 
                onClick={() => {
                  setShowModal(false);
                  setSelection(null);
                  setSelectionStart(null);
                  setCurrentIssue({ description: '', suggestion: '' });
                  setEditingIssueId(null);
                }}
              >
                取消
              </button>
              <button className="modal-button primary" onClick={saveIssue}>
                {editingIssueId ? '保存修改' : '添加问题'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
