import React, { useState, useRef, useCallback } from 'react';
import html2canvas from 'html2canvas';
import ExcelJS from 'exceljs';
import './index.css';

function App() {
  const [url, setUrl] = useState('');
  const [currentUrl, setCurrentUrl] = useState('');
  const [designImage, setDesignImage] = useState(null);
  const [imageOpacity, setImageOpacity] = useState(0.5);
  const [imageSize, setImageSize] = useState({ width: 400, height: 300 });
  const [imageAspectRatio, setImageAspectRatio] = useState(1);
  const [imagePosition, setImagePosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [dragStart, setDragStart] = useState(null);
  const [resizeStart, setResizeStart] = useState(null);
  const [isSelecting, setIsSelecting] = useState(false);
  const [selection, setSelection] = useState(null);
  const [selectionStart, setSelectionStart] = useState(null);
  const [issues, setIssues] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalScreenshot, setModalScreenshot] = useState(null);
  const [currentIssue, setCurrentIssue] = useState({ description: '', suggestion: '' });
  const [editingIssueId, setEditingIssueId] = useState(null);

  const previewRef = useRef();
  const fileInputRef = useRef();

  const loadUrl = () => {
    if (url.trim()) {
      setCurrentUrl(url.trim());
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const img = new Image();
        img.onload = () => {
          setDesignImage(e.target.result);

          // 计算宽高比
          const aspectRatio = img.width / img.height;
          setImageAspectRatio(aspectRatio);

          // 设置初始尺寸
          const maxWidth = 500;
          const maxHeight = 400;
          let width = img.width;
          let height = img.height;

          // 如果图片太大，按比例缩小
          if (width > maxWidth || height > maxHeight) {
            const scaleX = maxWidth / width;
            const scaleY = maxHeight / height;
            const scale = Math.min(scaleX, scaleY);
            width = Math.round(width * scale);
            height = Math.round(height * scale);
          }

          setImageSize({ width, height });
          setImagePosition({ x: 100, y: 100 });
        };
        img.src = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  };

  const triggerImageUpload = () => {
    fileInputRef.current.click();
  };

  const removeDesignImage = () => {
    setDesignImage(null);
    setImagePosition({ x: 0, y: 0 });
    setImageSize({ width: 400, height: 300 });
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleImageMouseDown = (e) => {
    if (e.target.classList.contains('resize-handle')) return; // 避免和缩放冲突
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
    setDragStart({
      x: e.clientX - imagePosition.x,
      y: e.clientY - imagePosition.y
    });
  };

  const handleResizeMouseDown = (e, direction) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);

    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      direction: direction,
      startSize: { ...imageSize },
      startPosition: { ...imagePosition }
    });
  };

  const handleMouseMove = useCallback((e) => {
    if (isDragging && dragStart) {
      setImagePosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }

    if (isResizing && resizeStart) {
      const deltaX = e.clientX - resizeStart.x;
      const deltaY = e.clientY - resizeStart.y;

      // 计算主要的缩放变化（取X和Y的平均值以保持等比例）
      const delta = (deltaX + deltaY) / 2;
      let scale = 1 + (delta / 200); // 缩放敏感度

      // 根据不同方向调整缩放方向
      switch (resizeStart.direction) {
        case 'se': // 右下角
          break; // 默认正向缩放
        case 'nw': // 左上角
          break; // 正向缩放
        case 'sw': // 左下角
          scale = 1 + ((-deltaX + deltaY) / 400);
          break;
        case 'ne': // 右上角
          scale = 1 + ((deltaX - deltaY) / 400);
          break;
        case 'e': // 右边
          scale = 1 + (deltaX / 200);
          break;
        case 'w': // 左边
          scale = 1 + (-deltaX / 200);
          break;
        case 's': // 下边
          scale = 1 + (deltaY / 200);
          break;
        case 'n': // 上边
          scale = 1 + (-deltaY / 200);
          break;
        default:
          break;
      }

      // 限制缩放范围
      scale = Math.max(0.2, Math.min(5, scale));

      // 计算新的等比例尺寸
      let newWidth = Math.round(resizeStart.startSize.width * scale);
      let newHeight = Math.round(newWidth / imageAspectRatio);

      // 最小尺寸限制
      newWidth = Math.max(50, newWidth);
      newHeight = Math.max(30, newHeight);

      // 根据缩放方向调整位置（确保对应的边固定不动）
      let newX = resizeStart.startPosition.x;
      let newY = resizeStart.startPosition.y;

      if (['nw', 'n', 'ne'].includes(resizeStart.direction)) {
        // 上边固定，调整Y位置
        newY = resizeStart.startPosition.y + (resizeStart.startSize.height - newHeight);
      }

      if (['nw', 'w', 'sw'].includes(resizeStart.direction)) {
        // 左边固定，调整X位置
        newX = resizeStart.startPosition.x + (resizeStart.startSize.width - newWidth);
      }

      setImageSize({ width: newWidth, height: newHeight });
      setImagePosition({ x: newX, y: newY });
    }

    if (isSelecting && selectionStart) {
      const rect = previewRef.current.getBoundingClientRect();
      const currentX = e.clientX - rect.left;
      const currentY = e.clientY - rect.top;

      setSelection({
        left: Math.min(selectionStart.x, currentX),
        top: Math.min(selectionStart.y, currentY),
        width: Math.abs(currentX - selectionStart.x),
        height: Math.abs(currentY - selectionStart.y)
      });
    }
  }, [isDragging, dragStart, isResizing, resizeStart, isSelecting, selectionStart]);

  const handleMouseUp = useCallback(async () => {
    setIsDragging(false);
    setIsResizing(false);
    setDragStart(null);
    setResizeStart(null);

    if (isSelecting && selection && selection.width > 10 && selection.height > 10) {
      setIsSelecting(false);

      try {
        const canvas = await html2canvas(previewRef.current, {
          useCORS: true,
          allowTaint: true,
          scale: 1
        });

        const screenshot = canvas.toDataURL('image/png');
        setModalScreenshot(screenshot);
        setShowModal(true);
        setCurrentIssue({ description: '', suggestion: '' });
        setEditingIssueId(null);
      } catch (error) {
        console.error('截图失败:', error);
        alert('截图失败，请重试');
      }
    }
  }, [isSelecting, selection]);

  React.useEffect(() => {
    if (isDragging || isResizing || isSelecting) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);

      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, isResizing, isSelecting, handleMouseMove, handleMouseUp]);

  const startSelection = () => {
    setIsSelecting(true);
    setSelection(null);
  };

  const cancelSelection = () => {
    setIsSelecting(false);
    setSelection(null);
    setSelectionStart(null);
  };

  const handlePreviewMouseDown = (e) => {
    if (isSelecting) {
      const rect = previewRef.current.getBoundingClientRect();
      setSelectionStart({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };


  const saveIssue = () => {
    if (currentIssue.description.trim() || currentIssue.suggestion.trim()) {
      const newIssue = {
        id: editingIssueId || Date.now().toString(),
        screenshot: modalScreenshot,
        description: currentIssue.description,
        suggestion: currentIssue.suggestion,
        status: '未验收'
      };

      if (editingIssueId) {
        setIssues(issues.map(issue => 
          issue.id === editingIssueId ? newIssue : issue
        ));
      } else {
        setIssues([...issues, newIssue]);
      }
    }

    setShowModal(false);
    setSelection(null);
    setSelectionStart(null);
    setModalScreenshot(null);
    setCurrentIssue({ description: '', suggestion: '' });
    setEditingIssueId(null);
  };

  const editIssue = (issue) => {
    setCurrentIssue({
      description: issue.description,
      suggestion: issue.suggestion
    });
    setModalScreenshot(issue.screenshot);
    setEditingIssueId(issue.id);
    setShowModal(true);
  };

  const deleteIssue = (issueId) => {
    if (window.confirm('确定要删除这个问题吗？')) {
      setIssues(issues.filter(issue => issue.id !== issueId));
    }
  };

  const exportToReport = async () => {
    if (issues.length === 0) {
      alert('没有问题记录可以导出');
      return;
    }

    try {
      // 创建新的工作簿
      const workbook = new ExcelJS.Workbook();
      const worksheet = workbook.addWorksheet('UI走查报告');

      // 设置列宽
      worksheet.columns = [
        { header: '问题ID', key: 'id', width: 10 },
        { header: '截图', key: 'screenshot', width: 30 },
        { header: '问题描述', key: 'description', width: 40 },
        { header: '修改建议', key: 'suggestion', width: 40 },
        { header: '验收状态', key: 'status', width: 15 }
      ];

      // 设置标题行样式
      const headerRow = worksheet.getRow(1);
      headerRow.height = 25;
      headerRow.eachCell((cell) => {
        cell.fill = {
          type: 'pattern',
          pattern: 'solid',
          fgColor: { argb: 'FF4472C4' }
        };
        cell.font = {
          color: { argb: 'FFFFFFFF' },
          bold: true,
          size: 12
        };
        cell.alignment = {
          vertical: 'middle',
          horizontal: 'center'
        };
        cell.border = {
          top: { style: 'thin' },
          left: { style: 'thin' },
          bottom: { style: 'thin' },
          right: { style: 'thin' }
        };
      });

      // 处理每个问题
      for (let index = 0; index < issues.length; index++) {
        const issue = issues[index];
        const rowIndex = index + 2; // 从第2行开始（第1行是标题）

        // 设置行高以适应图片
        const row = worksheet.getRow(rowIndex);
        row.height = 120; // 设置较大的行高以容纳图片

        // 添加问题ID
        row.getCell(1).value = index + 1;
        row.getCell(1).alignment = { vertical: 'middle', horizontal: 'center' };
        row.getCell(1).font = { bold: true, size: 11 };

        // 处理截图图片
        if (issue.screenshot) {
          try {
            // 将base64转换为buffer
            const base64Data = issue.screenshot.replace(/^data:image\/\w+;base64,/, '');
            const buffer = Buffer.from(base64Data, 'base64');

            // 添加图片到工作簿
            const imageId = workbook.addImage({
              buffer: buffer,
              extension: 'png',
            });

            // 在截图列中插入图片
            worksheet.addImage(imageId, {
              tl: { col: 1, row: rowIndex - 1 }, // 左上角位置（0-based）
              ext: { width: 200, height: 100 } // 图片尺寸
            });
          } catch (error) {
            console.error('处理图片失败:', error);
            row.getCell(2).value = '图片处理失败';
          }
        } else {
          row.getCell(2).value = '无截图';
        }

        // 添加问题描述
        row.getCell(3).value = issue.description || '无描述';
        row.getCell(3).alignment = { vertical: 'top', horizontal: 'left', wrapText: true };
        row.getCell(3).font = { size: 10 };

        // 添加修改建议
        row.getCell(4).value = issue.suggestion || '无建议';
        row.getCell(4).alignment = { vertical: 'top', horizontal: 'left', wrapText: true };
        row.getCell(4).font = { size: 10 };

        // 添加验收状态
        const status = issue.status || '未验收';
        row.getCell(5).value = status;
        row.getCell(5).alignment = { vertical: 'middle', horizontal: 'center' };
        row.getCell(5).font = { size: 10, bold: true };

        // 根据状态设置背景色
        let statusColor = 'FFFFF3CD'; // 默认未验收的颜色
        switch (status) {
          case '未修改':
            statusColor = 'FFF8D7DA';
            break;
          case '已修改待验收':
            statusColor = 'FFD4EDDA';
            break;
          case '已验收':
            statusColor = 'FFD1ECF1';
            break;
          default:
            statusColor = 'FFFFF3CD'; // 未验收
            break;
        }

        row.getCell(5).fill = {
          type: 'pattern',
          pattern: 'solid',
          fgColor: { argb: statusColor }
        };

        // 设置所有单元格的边框
        row.eachCell((cell) => {
          cell.border = {
            top: { style: 'thin' },
            left: { style: 'thin' },
            bottom: { style: 'thin' },
            right: { style: 'thin' }
          };
        });
      }

      // 添加报告信息
      const infoRow = worksheet.addRow([]);
      infoRow.getCell(1).value = `生成时间：${new Date().toLocaleString('zh-CN')}`;
      infoRow.getCell(1).font = { size: 9, color: { argb: 'FF666666' } };

      const summaryRow = worksheet.addRow([]);
      summaryRow.getCell(1).value = `问题总数：${issues.length} 个`;
      summaryRow.getCell(1).font = { size: 9, color: { argb: 'FF666666' } };

      // 生成文件
      const buffer = await workbook.xlsx.writeBuffer();
      const blob = new Blob([buffer], { 
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' 
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `UI走查报告_${new Date().toISOString().slice(0, 10)}.xlsx`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      alert('Excel走查报告导出成功！\\n\\n• 图片已直接嵌入Excel表格中\\n• 可以在Excel中直接查看和编辑\\n• 支持所有Excel功能');
    } catch (error) {
      console.error('导出Excel失败:', error);
      alert('导出Excel失败，请重试：' + error.message);
    }
  };

  return (
    <div className="app">
      <div className="top-bar">
        <input
          type="text"
          className="url-input"
          placeholder="请输入前端测试页面的URL地址..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && loadUrl()}
        />
        <button className="load-button" onClick={loadUrl}>
          加载页面
        </button>
      </div>

      <div className="main-content">
        <div className="preview-section">
          <div className="preview-controls">
            <input
              type="file"
              ref={fileInputRef}
              className="file-input"
              accept="image/*"
              onChange={handleImageUpload}
            />
            <button className="upload-button" onClick={triggerImageUpload}>
              上传设计图
            </button>
            {designImage && (
              <>
                <button className="upload-button" onClick={removeDesignImage} style={{backgroundColor: '#dc3545'}}>
                  删除设计图
                </button>
                <div className="control-group">
                  <label>透明度:</label>
                  <input
                    type="range"
                    className="control-slider"
                    min="0"
                    max="1"
                    step="0.1"
                    value={imageOpacity}
                    onChange={(e) => setImageOpacity(parseFloat(e.target.value))}
                  />
                  <span>{Math.round(imageOpacity * 100)}%</span>
                </div>
                <button 
                  className="upload-button" 
                  onClick={isSelecting ? cancelSelection : startSelection}
                  style={{backgroundColor: isSelecting ? '#dc3545' : '#ffc107', color: isSelecting ? 'white' : '#212529'}}
                >
                  {isSelecting ? '取消框选' : '开始框选'}
                </button>
              </>
            )}
          </div>


          <div 
            className="preview-container" 
            ref={previewRef}
            onMouseDown={handlePreviewMouseDown}
          >
            {currentUrl && (
              <iframe
                className="preview-iframe"
                src={currentUrl}
                title="网页预览"
                sandbox="allow-same-origin allow-scripts allow-forms"
              />
            )}

            {designImage && (
              <div
                className={`design-overlay ${isDragging ? 'dragging' : ''} ${isResizing ? 'resizing' : ''}`}
                style={{
                  left: imagePosition.x,
                  top: imagePosition.y,
                  width: imageSize.width,
                  height: imageSize.height,
                  opacity: imageOpacity,
                  position: 'absolute'
                }}
                onMouseDown={handleImageMouseDown}
              >
                <img
                  className="design-image"
                  src={designImage}
                  alt="设计图"
                  draggable={false}
                  style={{ 
                    width: '100%',
                    height: '100%',
                    objectFit: 'contain' // 保持宽高比
                  }}
                />

                {/* 8个缩放控制点 */}
                <div className="resize-handle nw" onMouseDown={(e) => handleResizeMouseDown(e, 'nw')} title="拖拽调整大小" />
                <div className="resize-handle n" onMouseDown={(e) => handleResizeMouseDown(e, 'n')} title="拖拽调整大小" />
                <div className="resize-handle ne" onMouseDown={(e) => handleResizeMouseDown(e, 'ne')} title="拖拽调整大小" />
                <div className="resize-handle e" onMouseDown={(e) => handleResizeMouseDown(e, 'e')} title="拖拽调整大小" />
                <div className="resize-handle se" onMouseDown={(e) => handleResizeMouseDown(e, 'se')} title="拖拽调整大小" />
                <div className="resize-handle s" onMouseDown={(e) => handleResizeMouseDown(e, 's')} title="拖拽调整大小" />
                <div className="resize-handle sw" onMouseDown={(e) => handleResizeMouseDown(e, 'sw')} title="拖拽调整大小" />
                <div className="resize-handle w" onMouseDown={(e) => handleResizeMouseDown(e, 'w')} title="拖拽调整大小" />
              </div>
            )}

            <div className={`selection-overlay ${isSelecting ? 'selecting' : ''}`}>
              {selection && (
                <div
                  className="selection-box"
                  style={{
                    left: selection.left,
                    top: selection.top,
                    width: selection.width,
                    height: selection.height
                  }}
                />
              )}
            </div>
          </div>
        </div>

        <div className="issues-panel">
          <div className="issues-header">
            <h3 className="issues-title">问题列表 ({issues.length})</h3>
            <button className="export-button" onClick={exportToReport} disabled={issues.length === 0}>
              导出走查报告
            </button>
          </div>

          <div className="issues-list">
            {issues.length === 0 ? (
              <div style={{ textAlign: 'center', color: '#666', marginTop: '50px' }}>
                暂无问题记录
                <br />
                <small>上传设计图后，点击"开始框选"来标记问题区域</small>
              </div>
            ) : (
              issues.map((issue) => (
                <div key={issue.id} className="issue-item">
                  <img
                    className="issue-screenshot"
                    src={issue.screenshot}
                    alt="问题截图"
                  />
                  <div className="issue-description">
                    <div className="issue-label">问题描述:</div>
                    <div className="issue-text">{issue.description || '无描述'}</div>
                  </div>
                  <div className="issue-suggestion">
                    <div className="issue-label">修改建议:</div>
                    <div className="issue-text">{issue.suggestion || '无建议'}</div>
                  </div>
                  <div className="issue-actions">
                    <button className="edit-button" onClick={() => editIssue(issue)}>
                      编辑
                    </button>
                    <button className="delete-button" onClick={() => deleteIssue(issue.id)}>
                      删除
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="modal-title">
              {editingIssueId ? '编辑问题' : '添加问题'}
            </h3>

            <img
              className="modal-screenshot"
              src={modalScreenshot}
              alt="截图"
            />

            <div className="form-group">
              <label className="form-label">问题描述</label>
              <textarea
                className="form-textarea"
                value={currentIssue.description}
                onChange={(e) => setCurrentIssue({...currentIssue, description: e.target.value})}
                placeholder="请具体描述发现的问题，如：按钮颜色不对、文字大小不符合设计稿等"
              />
              <div className="form-placeholder">
                提示：请详细描述问题，便于开发人员理解和修改
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">修改建议</label>
              <textarea
                className="form-textarea"
                value={currentIssue.suggestion}
                onChange={(e) => setCurrentIssue({...currentIssue, suggestion: e.target.value})}
                placeholder="请提供具体的修改建议，如：颜色改为#FF0000、字体大小改为16px等"
              />
              <div className="form-placeholder">
                提示：请提供具体的数值和参数，便于开发人员精确修改
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="modal-button secondary" 
                onClick={() => {
                  setShowModal(false);
                  setSelection(null);
                  setSelectionStart(null);
                  setCurrentIssue({ description: '', suggestion: '' });
                  setEditingIssueId(null);
                }}
              >
                取消
              </button>
              <button className="modal-button primary" onClick={saveIssue}>
                {editingIssueId ? '保存修改' : '添加问题'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
